import Button from "../UI/Button";
import Container from "../UI/Container";
import styles from './CreateTask.module.css'

const CreateTask = (props) => {

    const createTaskHandler = (event) => {
        event.preventDefault();
    }

    const nameChangeHandler = (event) => {
        console.log(event.target.value);
    }

    const commentChangeHandler = (event) => {
        console.log(event.target.value);
    }

    return (
        <Container className={styles.input}>
            <form onSubmit={createTaskHandler}>
                <label htmlFor="name">Task name</label>
                <input id="name" type="text" onChange={nameChangeHandler}/>
                <label htmlFor="comment">Commentary</label>
                <input id="comment" type="text" onChange={commentChangeHandler}/>
                <Button type="submit">Add task</Button>
            </form>
        </Container>
    )
}

export default CreateTask;
